import {
  animate,
  keyframes,
  state,
  style,
  transition,
  trigger
} from '@angular/animations';
import { Component } from '@angular/core';

import { Toast, ToastrService, ToastPackage } from 'ngx-toastr';

@Component({
  selector: 'custom-toast-reminder',
  styles: [`
    :host {
      pointer-events: all;
      cursor: pointer;
      overflow: auto;
      display: block;
    }
    :host.toast.toast-info {
      background-image: url("data:image/png;base64,")!important
    }

    :host.toast.toast-error {
      background-image: url("data:image/png;base64,")!important
    }

    :host.toast.toast-success {
      background-image: url("data:image/png;base64,")!important
    }

    :host.toast.toast-warning {
      background-image: url("data:image/png;base64,")!important
    }
  `],
  templateUrl: './patient-reminder-custom.component.html',
  animations: [
    trigger('flyInOut', [
      state(
        'inactive',
        style({
          display: 'none',
          opacity: 0
        })
      ),
      state('active', style({})),
      state('removed', style({ opacity: 0 })),
      transition(
        'inactive => active',
        animate('{{ easeTime }}ms {{ easing }}')
      ),
      transition('active => removed', animate('{{ easeTime }}ms {{ easing }}'))
    ]),
  ],
  preserveWhitespaces: false
})
export class PatientReminderCustomComponent extends Toast {
  public enrolling = false;
  _options: any = {};
  constructor(
    protected toastrService: ToastrService,
    public toastPackage: ToastPackage
  ) {
    super(toastrService, toastPackage);
    Object.assign(this._options, this.options);
  }

  public autoEnrollToProgram(event) {
    event.stopPropagation();
    this.enrolling = true;
    setTimeout(() => {
      this.toastPackage.triggerAction((this.options as any).reminder);
    }, 500);
    return false;
  }
}
